import { Component } from '@angular/core';

@Component({
  selector: 'app-libros',
  templateUrl: './libros.component.html',
  styleUrls: ['./libros.component.css']
})
export class LibrosComponent {
  tituloLibro: string = '';  // Propiedad para almacenar el valor del input
}
addEventListener("DOMContentLoaded", function () {

  // @ts-ignore
  document.getElementById("add").addEventListener("click", function () {

    // @ts-ignore
    document.getElementById("result").textContent= document.getElementById("libro").value

  })

})

<!doctype html>
<html lang="en">
<head>
  <meta charset="UTF-8">

</head>
<body>
  <p>Libros</p>
  Ingrese titulo <input type="text" name="libro" id="libro">
  <br>
  Nombre completo: <a id="result"></a>
 <br>
  <button id="add">Añadir</button>
</body>
</html>
