import { Component } from '@angular/core';

@Component({
  selector: 'app-libros',
  templateUrl: './libros.component.html',
  styleUrls: ['./libros.component.css']
})
export class LibrosComponent {
  tituloLibro: string = '';
  libros: string[] = [];
  addToArray(){
    this.libros.push(this.tituloLibro)
  }
}

addEventListener("DOMContentLoaded", function () {

  // @ts-ignore
  document.getElementById("add").addEventListener("click", function () {
    // @ts-ignore
    let valor = document.getElementById("libro").value
    // @ts-ignore
    document.getElementById("result").textContent= valor
    libros.push(valor)

  })

})


<body>
<p>Libros</p>
Ingrese titulo: <input type="text" id="titulo"  [(ngModel)]="titulo">
<p>El nombre completo: {{libros[libros.length-1]}}</p>

<table border="1">
  <tr>
    <td>TITULOS</td>
  </tr>
  <tr *ngFor="let titulo of titulosArray">
    <td >{{titulo}}</td>
  </tr>
</table>
<br>
<app-boton [label]="tituloBoton" (click)="addToArray()"></app-boton>
</body>
