import { Component } from '@angular/core';

@Component({
  selector: 'app-libros',
  templateUrl: './libros.component.html',
  styleUrls: ['./libros.component.css']
})
export class LibrosComponent {
  tituloLibro: string = '';
  libros: string[] = [];
  boton = "Añadir libro"
  addToArray(){
    this.libros.push(this.tituloLibro)
  }
}



<body>
<p>Libros</p>
Ingrese titulo: <input type="text" id="titulo" [(ngModel)]="tituloLibro">
<p>El nombre completo: {{libros[libros.length - 1]}}</p>

<h2>Libros</h2>
<ul *ngFor="let libro of libros">
  <li>{{libro}}</li>
</ul>
<br>
<app-boton [label]="boton" (click)="addToArray()"></app-boton>
</body>
